mapper.select:
SELECT d.id, d.system_software_version, d.wifi_address, d.bluetooth_address, d.ip_address, d.network_access_mode, d.serial_number, d.device_name, d.imei, d.created_at, d.battery_level, d.voltage, d.timestamp, d.model, d.status, d.wearable_status, d.charging_status, d.is_deleted, d.create_user, d.create_user_id, d.create_time, d.update_user, d.update_user_id, d.update_time, d.user_id, d.org_id, d.customer_id, u.user_name, u.org_name as user_org_name, u.real_name as user_real_name FROM t_device_info d LEFT JOIN sys_user u ON d.user_id = u.id WHERE d.is_deleted = 0
AND (d.customer_id = 0 OR d.customer_id = #{bo.customerId})
AND d.user_id = #{bo.userIdStr}
AND d.org_id = #{bo.orgId}
AND d.serial_number LIKE CONCAT(#{bo.serialNumber}, '%')
AND d.status = #{bo.status}
AND d.model = #{bo.model}
AND d.charging_status = #{bo.chargingStatus}
AND d.wearable_status = #{bo.wearableStatus}
AND u.user_name = #{bo.userName}
AND d.id IN (SELECT id FROM (SELECT id, ROW_NUMBER() OVER (PARTITION BY serial_number ORDER BY timestamp DESC) as rn FROM t_device_info WHERE is_deleted = 0) t WHERE rn = 1) ORDER BY d.update_time DESC

SELECT d.*, u.user_name, u.org_name as user_org_name, u.real_name as user_real_name FROM t_device_info d INNER JOIN sys_user u ON d.user_id = u.id WHERE d.is_deleted = 0 AND u.org_id = #{orgId}
AND d.customer_id = #{customerId}
AND d.status = #{status}
AND d.model = #{model}
ORDER BY d.update_time DESC

SELECT u.org_id, u.org_name, COUNT(DISTINCT d.serial_number) as device_count, COUNT(DISTINCT d.user_id) as user_count, COUNT(CASE WHEN d.status = 'ONLINE' THEN 1 END) as online_count, COUNT(CASE WHEN d.status = 'OFFLINE' THEN 1 END) as offline_count, COUNT(CASE WHEN d.wearable_status = 'WEARING' THEN 1 END) as wearing_count, COUNT(CASE WHEN d.charging_status = 'CHARGING' THEN 1 END) as charging_count, AVG(d.battery_level) as avg_battery_level FROM t_device_info d INNER JOIN sys_user u ON d.user_id = u.id WHERE d.is_deleted = 0
AND u.org_id IN
#{orgId}
AND d.customer_id = #{customerId}
GROUP BY u.org_id, u.org_name ORDER BY device_count DESC

SELECT d.serial_number, d.device_name, d.model, d.user_id, u.user_name, u.org_name, u.org_id, d.battery_level, d.status, d.wearable_status, d.charging_status, d.update_time, CASE WHEN d.battery_level < 10 THEN 'LOW_BATTERY' WHEN d.status = 'OFFLINE' THEN 'DEVICE_OFFLINE' WHEN d.wearable_status = 'NOT_WEARING' THEN 'NOT_WEARING' WHEN TIMESTAMPDIFF(HOUR, d.update_time, NOW()) > 24 THEN 'NO_DATA_UPDATE' ELSE 'NORMAL' END as health_status FROM t_device_info d INNER JOIN sys_user u ON d.user_id = u.id WHERE d.is_deleted = 0
AND u.org_id = #{orgId}
AND d.customer_id = #{customerId}
AND ( d.battery_level < 10 OR d.status = 'OFFLINE' OR d.wearable_status = 'NOT_WEARING' OR TIMESTAMPDIFF(HOUR, d.update_time, NOW()) > 24 )
ORDER BY CASE WHEN d.battery_level < 10 THEN 1 WHEN d.status = 'OFFLINE' THEN 2 WHEN d.wearable_status = 'NOT_WEARING' THEN 3 WHEN TIMESTAMPDIFF(HOUR, d.update_time, NOW()) > 24 THEN 4 ELSE 5 END, d.update_time DESC
