mapper.select:
SELECT
    m.*,
    u.user_name,
    u.org_name as user_org_name,
    u.real_name as user_real_name
FROM
    t_device_message m
INNER JOIN sys_user u ON m.user_id = u.id
WHERE m.is_deleted = 0
    AND u.org_id = #{orgId}

    AND m.customer_id = #{customerId}


    AND m.message_type = #{messageType}


    AND m.message_status = #{messageStatus}


    AND m.device_sn = #{deviceSn}

ORDER BY m.create_time DESC

SELECT u.org_id, u.org_name, COUNT(*) as message_count, COUNT(CASE WHEN m.message_status = 'SENT' THEN 1 END) as sent_count, COUNT(CASE WHEN m.message_status = 'DELIVERED' THEN 1 END) as delivered_count, COUNT(CASE WHEN m.message_status = 'READ' THEN 1 END) as read_count, COUNT(CASE WHEN m.message_type = 'ALERT' THEN 1 END) as alert_message_count FROM t_device_message m INNER JOIN sys_user u ON m.user_id = u.id WHERE m.is_deleted = 0
AND m.customer_id = #{customerId}
AND u.org_id IN
#{orgId}
AND m.create_time >= #{startTime}
AND m.create_time <= #{endTime}
GROUP BY u.org_id, u.org_name ORDER BY message_count DESC

SELECT m.user_id, u.user_name, u.org_name, u.org_id, COUNT(*) as total_messages, COUNT(CASE WHEN m.message_status = 'READ' THEN 1 END) as read_messages, AVG(TIMESTAMPDIFF(MINUTE, m.sent_time, m.received_time)) as avg_response_minutes, COUNT(CASE WHEN TIMESTAMPDIFF(MINUTE, m.sent_time, m.received_time) > 30 THEN 1 END) as delayed_responses FROM t_device_message m INNER JOIN sys_user u ON m.user_id = u.id WHERE m.is_deleted = 0 AND m.sent_time IS NOT NULL AND m.received_time IS NOT NULL
AND u.org_id = #{orgId}
AND m.customer_id = #{customerId}
AND m.create_time >= #{startTime}
AND m.create_time <= #{endTime}
GROUP BY m.user_id, u.user_name, u.org_name, u.org_id HAVING total_messages > 0 ORDER BY avg_response_minutes ASC
