mapper.select:
SELECT
    h.*,
    u.user_name,
    u.org_name as user_org_name,
    u.real_name as user_real_name
FROM
    t_user_health_data h
INNER JOIN sys_user u ON h.user_id = u.id
WHERE h.is_deleted = 0
    AND u.org_id = #{orgId}

    AND h.customer_id = #{customerId}


    AND h.device_sn = #{deviceSn}


    AND h.timestamp >= #{startTime}


    AND h.timestamp <= #{endTime}

ORDER BY h.timestamp DESC

    LIMIT #{limit}

SELECT u.org_id, u.org_name, COUNT(h.id) as data_count, COUNT(DISTINCT h.user_id) as user_count, COUNT(DISTINCT h.device_sn) as device_count, AVG(h.heart_rate) as avg_heart_rate, AVG(h.blood_oxygen) as avg_blood_oxygen, AVG(h.temperature) as avg_temperature, AVG(h.pressure_high) as avg_pressure_high, AVG(h.pressure_low) as avg_pressure_low, SUM(h.step) as total_steps, SUM(h.calorie) as total_calories FROM t_user_health_data h INNER JOIN sys_user u ON h.user_id = u.id WHERE h.is_deleted = 0
AND u.org_id IN
#{orgId}
AND h.customer_id = #{customerId}
AND h.timestamp >= #{startTime}
AND h.timestamp <= #{endTime}
GROUP BY u.org_id, u.org_name ORDER BY data_count DESC

SELECT h.user_id, u.user_name, u.org_name, u.org_id, DATE(h.timestamp) as health_date, AVG(h.heart_rate) as avg_heart_rate, AVG(h.blood_oxygen) as avg_blood_oxygen, AVG(h.temperature) as avg_temperature, MAX(h.pressure_high) as max_pressure_high, MIN(h.pressure_low) as min_pressure_low, SUM(h.step) as daily_steps, SUM(h.calorie) as daily_calories, COUNT(*) as record_count FROM t_user_health_data h INNER JOIN sys_user u ON h.user_id = u.id WHERE h.is_deleted = 0
AND h.user_id = #{userId}
AND u.org_id = #{orgId}
AND h.customer_id = #{customerId}
AND h.timestamp >= #{startTime}
AND h.timestamp <= #{endTime}
GROUP BY h.user_id, u.user_name, u.org_name, u.org_id, DATE(h.timestamp) ORDER BY health_date DESC, h.user_id

SELECT h.id, h.user_id, u.user_name, u.org_name, u.org_id, h.device_sn, h.timestamp, h.heart_rate, h.blood_oxygen, h.temperature, h.pressure_high, h.pressure_low, CASE WHEN h.heart_rate > 120 OR h.heart_rate < 50 THEN 'HEART_RATE_ABNORMAL' WHEN h.blood_oxygen < 90 THEN 'LOW_BLOOD_OXYGEN' WHEN h.temperature > 37.5 OR h.temperature < 35.0 THEN 'TEMPERATURE_ABNORMAL' WHEN h.pressure_high > 160 OR h.pressure_low > 100 THEN 'HIGH_BLOOD_PRESSURE' ELSE 'OTHER' END as abnormal_type FROM t_user_health_data h INNER JOIN sys_user u ON h.user_id = u.id WHERE h.is_deleted = 0 AND ( h.heart_rate > 120 OR h.heart_rate < 50 OR h.blood_oxygen < 90 OR h.temperature > 37.5 OR h.temperature < 35.0 OR h.pressure_high > 160 OR h.pressure_low > 100 )
AND u.org_id = #{orgId}
AND h.customer_id = #{customerId}
AND h.timestamp >= #{startTime}
AND h.timestamp <= #{endTime}
ORDER BY h.timestamp DESC
LIMIT #{limit}
