mapper.select:
SELECT mc.*
FROM sys_org_manager_cache mc
WHERE mc.org_id = #{orgId}
  AND mc.customer_id = #{customerId}
  AND mc.is_active = 1

    AND mc.role_type = #{roleType}

ORDER BY mc.org_level ASC, mc.user_id ASC

SELECT mc.*
FROM sys_org_manager_cache mc
WHERE mc.org_id IN

    #{orgId}

  AND mc.customer_id = #{customerId}
  AND mc.is_active = 1

    AND mc.role_type = #{roleType}

ORDER BY mc.org_level ASC, mc.org_id ASC, mc.user_id ASC

SELECT DISTINCT mc.*
FROM sys_org_closure c
INNER JOIN sys_org_manager_cache mc ON c.ancestor_id = mc.org_id
WHERE c.descendant_id = #{orgId}
  AND c.customer_id = #{customerId}
  AND mc.customer_id = #{customerId}
  AND mc.is_active = 1

    AND mc.role_type = #{roleType}

ORDER BY c.depth ASC, mc.org_level ASC, mc.user_id ASC

SELECT mc.*
FROM sys_org_manager_cache mc
WHERE mc.user_id = #{userId}
  AND mc.customer_id = #{customerId}
  AND mc.is_active = 1
ORDER BY mc.org_level ASC, mc.org_id ASC
